<script lang="ts">
  import { defineComponent } from 'vue'
  export default defineComponent({
    name: 'NotFound',
    data() {
      return {
        circles: [],
        interval: 0,
      }
    },

    mounted() {
      this.circles = this.$el.querySelectorAll('.not-found__circle')
      this.startAnimation()
    },

    beforeUnmount() {
      window.clearInterval(this.interval)
    },

    expose: [],
    methods: {
      startAnimation() {
        const delay = this.randomNumber(3, 4) * 1000
        this.interval = window.setTimeout(() => {
          this.animateRandomCircles()
          this.startAnimation()
        }, delay)
      },

      animateRandomCircles() {
        const circles: Element[] = []
        while (circles.length < 3) {
          const index = this.randomNumber(0, this.circles.length - 1)
          const circle = this.circles[index]
          circles.push(circle)
        }
        circles
          .filter(this.uniqueElementFilter)
          .forEach(this.animateCircle)
      },

      animateCircle(circle: Element) {
        const animationClass = 'not-found__circle--animate'
        circle.addEventListener(
          'animationend',
          () => {
            circle.classList.remove(animationClass)
          },
          { once: true },
        )
        circle.classList.add(animationClass)
      },

      randomNumber(min: number, max: number) {
        return Math.floor(Math.random() * (max - min + 1) + min)
      },

      uniqueElementFilter(
        value: Element,
        index: number,
        array: Element[],
      ) {
        return array.indexOf(value) === index
      },
    },
  })
</script>

<style>
.not-found {
  display: flex;
  align-items: center;
  justify-content: center;
  background:#0a2053;
}

@media screen and (max-width: 1024px) {
  .not-found {
    background-size: 150px auto, 500px auto;
  }
}

.not-found__message {
  width: 600px;
  max-width: 80%;
  margin-top: -15%;
}

.not-found__circle {
  transform-box: fill-box;
  transform-origin: center;
  opacity: 0;
}

.not-found__circle--animate {
  animation: pulse-black 1.5s;
}

@keyframes pulse-black {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: scale(1);
    opacity: 0;
  }
}
</style>